class TodoList {
    tick = 0

    increment() {
        this.tick++;
    }
}
decorate(TodoList,{
    tick:observable,
    increment:action.bound
})

const ticker = new Ticker()
setInterval(ticker.increment, 1000)


export default withRouter(TodoList);
