import React, { Component } from "react";
import Popup from "reactjs-popup";

class Event extends Component{
  constructor(props){
    super(props)
    this.state={
      name:"",
      desc:"",
      fromDate:new Date(),
      toDate:new Date(),
      fromTime:new Date().toLocaleString(),
      toTime:new Date().toLocaleString()
    }
    this.event=props.event;
  }
  event=(event)=>{
    event.preventDefault();
    console.log(this.state);
    this.event(this.state);
  }
  handleName=(event)=>{
    this.setState({name: event.target.value});
  }
  handleDesc=(event)=>{
    this.setState({desc: event.target.value});
  }
  handleFromDate=(event)=>{
    this.setState({fromDate: event.target.value});
  }
  handleToDate=(event)=>{
    this.setState({toDate: event.target.value});
  }
  handleToTime=(event)=>{
    this.setState({toTime: event.target.value});
  }
  handleFromTime=(event)=>{
    this.setState({fromTime: event.target.value});
  }
  render(){
    return(
      <Popup style={{width:"auto"}} trigger={<button>Create an Event</button>} position="right top">
    <form onSubmit={this.event}>
        Event name:
        <input type="text" value={this.state.name} onChange={this.handleName} required/>
        Event Desc:
        <input type="text" value={this.state.desc} onChange={this.handleDesc} required/>
        From
        <input type="date" value={this.state.fromDate} onChange={this.handleFromDate}/>
        <input type="time" value={this.state.fromTime} onChange={this.handleFromTime}/>
        To
        <input type="date" value={this.state.toDate} onChange={this.handleToDate}/>
        <input type="time" value={this.state.toTime} onChange={this.handleToTime}/>
        <input type="submit"/>
    </form>
  </Popup>
    )
  }
}

export default Event;
