import React from 'react';
import moment from 'moment';
import { withRouter } from "react-router";



function Weekdayshortname(){
  let weekdayshort = moment.weekdaysShort();
  alert(weekdayshort);
  console.log(weekdayshort);
  return (
    weekdayshort.map(day => {
    return (
      <th key={day} className="week-day">
       {day}
      </th>
    );
 }));
} 
class Calendar extends React.Component {
  constructor(props){
    super(props);
    this.state={
      dateObject: moment()
    }
  }
  firstDayOfMonth = () => {
    alert("hjfdj");
    console.log("hii");
    console.log(this.state.dateObject);
    let dateObject = this.state.dateObject;
    let firstDay = moment(this.state.dateObject)
                .startOf("month")
                .format("d"); 
    console.log(firstDay);
  return firstDay;
  };
    render() {
      return (
        <div>
          <h2>Calendar</h2>
          <table><Weekdayshortname/></table>

          <p>{moment(this.state.dateObject)
                .startOf("april")
                .format("d")}</p>
        </div>
      );
    }
}
export default withRouter(Calendar);
