import React from 'react';
import { withRouter } from "react-router";
import { observable, decorate, action,computed } from "mobx";
import {inject, observer,Observer} from "mobx-react";
import './dark.css';


class TodoList extends React.Component{
    constructor(props){
        super(props);
        this.state={
            darkmode:true
        }
        this.state.darkmode=this.initialMode();
        window.localStorage.setItem("mode", this.state.darkmode);
    }
    initialMode=()=>{
        let some=this.getPreferredone();
        console.log(some);
        if(window.localStorage.getItem("mode")==="true"){
            return true;
        }
        else{
            return false;
        }
    }
    getPreferredone=()=>{
        if(!window.matchMedia){
            return "hii";
        }
        return window.matchMedia("(prefers-color-scheme: dark)").matches;
    }
    change=()=>{
        window.localStorage.setItem("mode", !this.state.darkmode);
        this.setState(()=>({
            darkmode: !this.state.darkmode
        }));

    }
    render(){
        console.log(this.state.darkmode,"render",window.localStorage.getItem("mode"))
        return(
        <div className={this.state.darkmode ? "light-mode" : "dark-mode"}>
            <button onClick={this.change}>toggle</button>
            <h1>darkmode</h1>
        </div>
        );
    }
}

